INSERT INTO `pages` VALUES (1,0,'Главная','','','','','',''),(4,0,'Контакты','<table cellspacing=\"10\" cellpadding=\"10\">\r\n<tbody>\r\n<tr>\r\n<td style=\"width: 75px;\">Адрес</td>\r\n<td style=\"text-align: center;\">Н.Новгород,&nbsp;ул. Тимирязева, 29Б</td>\r\n</tr>\r\n<tr>\r\n<td>Телефон</td>\r\n<td style=\"text-align: left;\">8 (831) 4-397-397</td>\r\n</tr>\r\n<tr>\r\n<td>E-mail</td>\r\n<td style=\"text-align: left;\">info@senardgy.ru</td>\r\n</tr>\r\n</tbody>\r\n</table>','contacts','Контакты компании Формула КД','','','contact'),(36,0,'Партнеры','<p>Страница с описанием партнеров</p>','partnery','','','',''),(52,38,'Сэнарджи ПпС','','senardzi-pps','','','','system-pps'),(53,38,'Сэнарджи ПпС-3','','senardzi-pps-3','','','','system-pps3'),(54,38,'Сэнарджи МвС','','senardzi-mvs','','','','system-mvs'),(9,0,'О нас','<p align=\"justify\">Компания <strong><span style=\"color: #003399;\">\"Сэнарджи&reg;\"</span></strong> является разработчиком и производителем систем наружной теплоизоляции фасадов зданий <strong><span style=\"color: #003399;\">\"Сэнарджи&reg; МвС\"</span></strong> и <strong><span style=\"color: #003399;\">\"Сэнарджи&reg; ПпС-3\"</span></strong>. Системы утепления <strong><span style=\"color: #003399;\">\"Сэнарджи&reg;\"</span></strong> полностью сертифицированы и имеют Техническое свидетельство Росстроя России.</p>\r\n<p align=\"justify\">Системы утепления <strong><span style=\"color: #003399;\">\"Сэнарджи&reg; МвС \"</span></strong> (<strong>утепление фасадов и стен</strong>минераловатной плитой) и <strong><span style=\"color: #003399;\">\"Сэнарджи&reg; ПпС-3\"</span></strong> (утепление фасадов пенопластом (пенополистиролом)) предназначены для применения на вновь строящихся и реконструируемых зданиях и сооружениях.</p>\r\n<p align=\"justify\"><strong>Утепление стен фасадов</strong> пенополистиролом возможно для зданий всех степеней огнестойкости и классов функциональной и конструктивной пожарной опасности за исключением зданий функциональной пожарной опасности Ф1.1. (детские дошкольные учреждения, специализированные дома престарелых и инвалидов, больницы и т.д.) и Ф4.1. (школы, внешкольные учебные заведения, средние специальные учебные заведения и т.д.). Утепление фасадов зданий минераловатной плитой возможно для зданий всех степеней огнестойкости и классов функциональной и конструктивной пожарной опасности без ограничений.</p>\r\n<p align=\"justify\">Утепление фасадов здания с применением систем <strong><span style=\"color: #003399;\">\"Сэнарджи&reg;\"</span></strong> позволяет обеспечить снижение материальных затрат при строительстве (уменьшаются нагрузки на фундамент, увеличивается внутренняя полезная площадь здания) и в процессе эксплуатации здания (уменьшаются затраты на отопление и кондиционирование, повышается звукоизоляция). Кроме того утепление фасадов домов позволяет изменить и дополнить их архитектурный облик - возможно изготовление колонн, рустов, карнизов, наличников и других архитектурных деталей практически независимо от сложности их формы и рисунка; возможны любые цветовые решения.</p>\r\n<p align=\"justify\">Компания <strong><span style=\"color: #003399;\">\"Сэнарджи&reg;\"</span></strong> также предлагает услуги по монтажу систем утепления <strong><span style=\"color: #003399;\">\"Сэнарджи МвС \"</span></strong> и <strong><span style=\"color: #003399;\">\"Сэнарджи ПпС-3\"</span></strong>: утепление фасадов многоэтажных жилых домов, административных зданий, коттеджей (в т.ч. утепление фасадов деревянных домов) любой сложности.</p>\r\n<p align=\"justify\"><strong>Утепление фасадов</strong> снаружи с помощью систем утепления <strong><span style=\"color: #003399;\">\"Сэнарджи&reg;\"</span></strong> - это гарантия тепла и уюта в любом доме!</p>\r\n<p><br /><br /><br /><br /></p>\r\n<p><strong>Виды деятельности:</strong></p>\r\n<p align=\"justify\"><strong>Утепление фасадов стен домов</strong>, утепление стен и фасадов зданий и коттеджей пенополистеролом (пенопластом), утепление стен минераловатной плитой, утепление стен деревянных домов, утепление многоэтажных домов, утепление стен снаружи.</p>\r\n<p align=\"justify\">Изготовление и монтаж декоративных элементов любой сложности.</p>','about','О компании','','',''),(40,0,'Реставрация','<p>Раздел в разработке</p>','restavracia','','','',''),(41,0,'Техническая документация','<p>Раздел в разработке</p>','tehniceskaa-dokumentacia','','','',''),(42,0,'Техническая поддержка','<p>По всем вопросам, возникающим при проектировании зданий и сооружений с использованием систем &laquo;Сэнарджи&reg;&raquo; и при монтаже систем &laquo;Сэнарджи&reg;&raquo; вы можете обратится в ООО &laquo;Сэнарджи-НН&raquo;. У нас индивидуальный подход к каждому клиенту! Мы готовы поддержать наших клиентов и партнеров на всех этапах работы. Наш технический отдел обеспечивает сопровождение объектов любой сложности. Сотрудники строительной организации , выполняющей монтаж системы &laquo;Сэнарджи&reg;&raquo;, должны пройти обучение (инструктаж) технологии монтажа систем &laquo;Сэнарджи&reg;&raquo;. По завершении обучения сотрудникам строительной организации выдается свидетельство установленного образца.</p>','tehniceskaa-podderzka','','','',''),(43,0,'Текстуры и цвета','<p>Раздел в разработке</p>','tekstury-i-cveta','','','','tekstury-i-cveta'),(44,0,'Фотогалерея','<p>Раздел в разработке</p>','fotogalerea','','','','photogallery'),(39,0,'Продукция','<p>Раздел в разработке</p>','produkcia','','','','products'),(38,0,'Системы утепления','<p>Раздел в разработке</p>','sistemy-uteplenia','','','','systems'),(45,44,'Выполненные работы','','vypolnennye-raboty','','','','vypolnennye-raboty'),(46,39,'Грунтовки (праймер)','','gruntovki-prajmer','','','',''),(82,44,'Многоэтажные жилые','','mnogoetaznye-zilye','','','','mnogoetaznye-zilye'),(47,39,'Адгезив','','adgeziv','','','',''),(48,77,'Армирующая стеклотканевая сетка','<div class=\"page\">\r\n<div class=\"layoutArea\">\r\n<div class=\"column\">\r\n<div class=\"page\">\r\n<div class=\"layoutArea\">\